import React, { Component } from 'react';


class WordCount extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        value : null
      }
      this.handleKeypress = this.handleKeypress.bind(this);
      this._onFocus = this._onFocus.bind(this);
      this._onBlur = this._onBlur.bind(this);
      this.placeholder = "1. Call family \n 2. Finish Stripe Assignment \n"
    }

    handleKeypress(event) {
      console.log('*****', event.target.value);
      this.setState({ value:event.target.value });
    }

    componentDidMount() {
      this.setState({ value: this.placeholder})
    }

    _onFocus() {
      console.log(this)
    }

    _onBlur(){
      console.log("Blur")
    }

    render(){
      let count = 0,
          length = this.state.value?this.state.value.length:0;
      return(
        <div>
          <textarea id="" 
            onChange={(event)=>this.handleKeypress(event)} 
            defaultValue={this.placeholder}
            onFocus={this._onFocus} 
            onBlur={this._onBlur}>
            {this.state.value}
          </textarea>
          <div>Count: {length}</div>
          <div>this.state.value: {this.state.value}</div>
        </div>
      );
    }
  }

  export default WordCount
