import React, { Component } from 'react';


class WordCount extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
        value : null
      }
      this.handleKeypress = this.handleKeypress.bind(this);
      this._onFocus = this._onFocus.bind(this);
      this._onBlur = this._onBlur.bind(this);
      this.setValue = this.setValue.bind(this);
      this.placeholder = "1. Call family \n2. Finish Stripe Assignment \n";
      this.startValue = "1. ";
      this.textInput = React.createRef();
      this.costPerLine = 1;
    }

    handleKeypress(event) {
      this.setState({ value:event.target.value });
    }

    componentDidMount() {
      this.textInput.current.placeholder = this.placeholder
    }

    setValue(newValue){
      this.textInput.current.value = newValue
      this.state.value = newValue
    }

    _onFocus() {
      if(!this.state.value){
        this.setValue(this.startValue);
      }
    }

    _onBlur() {
      if(this.state.value == this.startValue){
        this.setValue("")
      }
    }

    calculateWordCount(text) {
      text = text.trim()
      text = text.replace(/\d+\.+/g, '');
      text = text.replace(/\s+/g,' ');
      text = text.trim()
      return text?text.split(" ").length:0;
      return 0
    }

    render(){
      let count = 0,
          length = this.state.value?this.calculateWordCount(this.state.value):0,
          cost = length * this.costPerWord
      return(
        <div>
          <textarea id="" 
            onChange={(event)=>this.handleKeypress(event)} 
            onFocus={this._onFocus} 
            onBlur={this._onBlur}
            ref={this.textInput}
          >
            {this.state.value}
          </textarea>
          <div>Count: {length}</div>
        </div>
      );
    }
  }

  export default WordCount
