import logo from './logo.png';
import './App.css';
import React from 'react';


class WordCount extends React.Component {
  constructor(props) { 
    super(props);
    this.state = {
      value : null
    }
    this.handleKeypress = this.handleKeypress.bind(this);
  }

  handleKeypress(event) {
    console.log('*****', event.target.value);
    this.setState({ value:event.target.value });
  }

  render(){
    let count = 0,
        length = this.state.value?this.state.value.length:0;
    return(
      <div>
        <textarea onChange={(event)=>this.handleKeypress(event)}>{this.state.value}</textarea>
        <div>Count: {length}</div>
        <div>this.state.value: {this.state.value}</div>
      </div>
    );
  }
}

function App() {
  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p className="Intro-text">
          Can’t figure out what to focus on? Get some of the top PMs in the world to prioritise your life for you.
        </p>
        <p className="Intro-text">
          Just enter a list below and make your payment (0.20 per word) -- and we’ll respond with a reprioritised list within 24 hours.
        </p>
        <form>
          <WordCount></WordCount>
        </form>
      </header>
    </div>
  );
}

export default App;
